import {useContext} from "react";
import {Context} from '../Context'
import {db} from "../firebase"

function Chooser() {
    const {currentId} = useContext(Context)

    const addMovie=()=>{
        db.collection("User").doc(currentId).update({
            AddedItem:firebase.firestore.FieldValue.arrayUnion("aNameAddedViaChooser")
        })
    }
    return (
        <div className="App">
        <header className="App-header">
            <h3>Hello from Chooser</h3>
            <h3>current Id is {currentId}</h3>
            <button onClick={addMovie}>add Movie</button>
        </header>
        <button><Link to = "/cheecker">Go to Cheecker</Link></button>
        </div>
    );
}

export default Chooser;
