import {useContext, useEffect, useState} from "react";
import {Context} from '../Context'
import {db} from "../firebase";
function Cheecker() {
    //const {checkMatches} = useContext(Context)
    //const {result} = useContext(Context)
    const {obj} = useContext(Context)
    const [data, setData] = useState({})
    const [result, setResult] = useState([])
    useEffect(()=>{
        db.collection("User").onSnapshot(snapshot=>{
            let changes = snapshot.docChanges()
            changes.forEach(item=>{
                if(item.doc.id==9){
                    setData(item.doc.data())
                }
            })
        })
    },[])
    useEffect(()=>{
        let bunch = []
        for(const name in data){
            bunch.push(data[name])
            //console.log(data[name])
        }
        checkMatches(bunch)
        console.log(result)
    },[data])
    const checkMatches = moviesArr =>{
        var resArr = [];
        if(moviesArr.length>0){
            for (var i = moviesArr[0].length - 1; i > 0; i--) {
                for (var j = moviesArr.length - 1; j > 0; j--) {
                    if (moviesArr[j].indexOf(moviesArr[0][i]) == -1) {
                        break;
                    }
                }
                if (j === 0) {
                    setResult(...(moviesArr[0][i]));
                }
            }
        }
    }

    let i = result.map(r=><h9>{r}</h9>)
    return (
        <div className="App">
        <header className="App-header">
            <h1>Cheecker page</h1>
        </header>
        </div>
    );
}

export default Cheecker;
