import {useContext} from "react";
import {Context} from '../Context'

export default function MovieCard({movie}){
    const {addMovie} = useContext(Context)
    return (
         <div className="card">
             <div className="text-center mb-4 absolute -top-16 right-1/2 transform translate-x-1/2">
                 <div className="block relative">
                    <img className="mx-auto object-cover rounded-lg h-40 w-40  border-2 border-white dark:border-gray-800"
                        src={`https://image.tmdb.org/t/p/w185_and_h278_bestv2/${movie.poster_path}`}
                        alt={movie.title + ' poster'}
                    />
                 </div>
             </div>
            <div className="bg-white dark:bg-gray-800 rounded-lg shadow px-8 py-4 pt-24">
                <div className="text-center">
                    <h3 className="card--title">{movie.title}</h3>
                    {/* <h3 className="card--title">{movie.id}</h3> */}
                    <button onClick={()=>addMovie(movie.title)}>add Movie</button>
                    <p className = "card--para"><small>RELEASE DATE: {movie.release_date}</small></p>
                    <p className = "card--para"><small>RATING: {movie.vote_average}</small></p>
                    <p className="card--para">{movie.overview}</p>

                </div>
            </div>

        </div>
    )
}
